document.addEventListener("DOMContentLoaded",()=>{let navigationBar=document.querySelector(".navbar");window.addEventListener("scroll",()=>{window.scrollY>30?navigationBar.classList.add("changing-header"):navigationBar.classList.remove("changing-header")})}),document.addEventListener("DOMContentLoaded",()=>{document.querySelector("#downloading-btn-recipe").addEventListener("click",()=>{html2canvas(document.querySelector(".current-recipe")).then(canvas=>{let base64image=canvas.toDataURL("image/jpeg"),pdf=new jsPDF("p","px",[1600,1131]);pdf.addImage(base64image,"JPEG",15,15,1110,800),pdf.save("8bit-burger_recette.pdf")})})}),document.addEventListener("DOMContentLoaded",()=>{document.querySelector("#download-btn-shopping-list").addEventListener("click",()=>{html2canvas(document.querySelector("#writing-shopping-list")).then(canvas=>{let pdf;new jsPDF("p","pt","a4").save("liste_de_courses.pdf")})})}),document.addEventListener("DOMContentLoaded",()=>{(()=>{"use strict";let forms=document.querySelectorAll(".needs-validation");Array.from(forms).forEach(form=>{form.addEventListener("submit",event=>{form.checkValidity()||(event.preventDefault(),event.stopPropagation()),form.classList.add("was-validated")},!1)})})()});